%asterixdb
-- Output "success" indicates that the DDL statements was successful

DROP DATAVERSE TinySocial IF EXISTS;
CREATE DATAVERSE TinySocial;
USE TinySocial;

CREATE TYPE ChirpUserType AS {
    screenName: string,
    lang: string,
    friendsCount: int,
    statusesCount: int,
    name: string,
    followersCount: int
};

CREATE TYPE ChirpMessageType AS closed {
    chirpId: string,
    user: ChirpUserType,
    senderLocation: point?,
    sendTime: datetime,
    referredTopics: {{ string }},
    messageText: string
};

CREATE TYPE EmploymentType AS {
    organizationName: string,
    startDate: date,
    endDate: date?
};

CREATE TYPE GleambookUserType AS {
    id: int,
    alias: string,
    name: string,
    userSince: datetime,
    friendIds: {{ int }},
    employment: [EmploymentType]
};

CREATE TYPE GleambookMessageType AS {
    messageId: int,
    authorId: int,
    inResponseTo: int?,
    senderLocation: point?,
    message: string
};

%asterixdb
USE TinySocial;

--In case of an error, a message should should indicate what went wrong

CREATE TYPE GleambookMessageType AS {
    messageId: int,
    authorId: int,
    inResponseTo: int?,
    senderLocation: point?,
    message: string
};

%asterixdb
USE TinySocial;

-- Lets create some datasets with thier indexes

CREATE DATASET GleambookUsers(GleambookUserType)
    PRIMARY KEY id;

CREATE DATASET GleambookMessages(GleambookMessageType)
    PRIMARY KEY messageId;

CREATE DATASET ChirpUsers(ChirpUserType)
    PRIMARY KEY screenName;

CREATE DATASET ChirpMessages(ChirpMessageType)
    PRIMARY KEY chirpId
    hints(cardinality=100);

CREATE INDEX gbUserSinceIdx on GleambookUsers(userSince);
CREATE INDEX gbAuthorIdx on GleambookMessages(authorId) TYPE btree;
CREATE INDEX gbSenderLocIndex on GleambookMessages(senderLocation) TYPE rtree;
CREATE INDEX gbMessageIdx on GleambookMessages(message) TYPE keyword;

%asterixdb
USE TinySocial;

SELECT VALUE user
FROM GleambookUsers user
WHERE user.id = 8;

%asterixdb
USE TinySocial;

SELECT VALUE user
FROM GleambookUsers user
WHERE user.id >= 2 AND user.id <= 4;

%asterixdb
+flat
USE TinySocial;

SELECT user.name AS uname, msg.message AS message
FROM GleambookUsers user, GleambookMessages msg
WHERE msg.authorId = user.id;

%asterixdb
+flat
USE TinySocial;

SELECT uid AS user, COUNT(cm) AS count
FROM ChirpMessages cm
GROUP BY cm.user.screenName AS uid;
